mapper.update:
UPDATE em_define_role
SET state = -1

    ,last_modifyer_id = #{loginUser.fid}

WHERE fid IN

    #{item}

mapper.select:
SELECT

FROM em_define_role dr
left JOIN em_user_role ur on dr.fid = ur.define_role_id
WHERE
    ur.user_account_id = #{userAccountId}
    and  ur.state = #{stateVal}
